import IQosMonitor from './IQosMonitor';
/**
 * @internal
 */
export default class QosMonitor implements IQosMonitor {
    qos: any;
    /**
   * Creates a QoS monitor (initializes it and logs the start event)
   * @param	scenarioName	Unique name of the QoS scenario
   * You will see start tag: ModernPublish.RenderPublishPage.Start
   */
    constructor(scenarioName: string);
    /**
     * QoS monitor ends with success
     * You will see success tag: ModernPublish.RenderPublishPage.Success
     */
    writeSuccess(): void;
    /**
     * QoS monitor ends with failure
     * You will see success tag: ModernPublish.RenderPublishPage.<failuretag>.Failure.
     * <failuretag> will be filled with @param tagName
     */
    writeUnexpectedFailure(tagNameSuffix?: string, ex?: Error, extraData?: {
        [key: string]: any;
    }): void;
    writeExpectedFailure(tagNameSuffix?: string, ex?: Error, extraData?: {
        [key: string]: any;
    }): void;
}
