import { ServiceScope } from '@microsoft/sp-client-base';
import IWebPartHost from '../../../../common/webParts/IWebPartHost';
import { WebPartConfigurationEvent } from '../../../../common/webParts/PropertyPaneDefinitions';
/**
 * CanvasWebPartHost is the IWebPartHost implementation used by the Canvas. Each Canvas
 * will instantiate their own CanvasWebPartHost, which means the web parts are scoped by
 * Canvas.
 */
export default class CanvasWebPartHost implements IWebPartHost {
    private _handleCanvasChanged;
    private _handleResize;
    private _reclaimFocus;
    private _serviceScope;
    private _environment;
    /**
     * @param handleCanvasChanged - Optional. Callback invoked when web parts are interacted with.
     */
    constructor(serviceScope: ServiceScope, handleResize: (element: HTMLElement, duration: number, margin?: number) => void, handleCanvasChanged?: () => void, reclaimFocus?: () => void);
    /**
     * @see IWebPartHost
     */
    serviceScope: ServiceScope;
    /**
     * @see IWebPartHost
     */
    setDirty(wpInstanceId: string): void;
    /**
     * @see IWebPartHost
     */
    webPartConfigurationEventCallback(event: WebPartConfigurationEvent): void;
}
