/**
 * Context for all client side components
 */
export default class ClientSideComponentContext<TManifest> {
    private _domElement;
    private _manifest;
    /**
     * @internal
     */
    constructor(domElement: HTMLElement, manifest: TManifest);
    /**
     * Reference to the DOM element that hosts this client side component.
     */
    domElement: HTMLElement;
    /**
     * Client side component manifest
     */
    manifest: TManifest;
}
