import * as React from 'react';
import { DisplayMode } from '@microsoft/sp-client-base';
import { IWebPartProps } from './IWebPartProps';
import IWebPartContext from './IWebPartContext';
import BaseClientSideWebPart from './BaseClientSideWebPart';
/**
 * React web part props
 *
 * @alpha
 */
export interface IReactWebPartProps extends IWebPartProps {
    /**
     * The display mode for the summary links component. It is either Read or Edit.
     * This value is read from web part and passed to here.
     */
    mode: DisplayMode;
}
/**
 * Implement some common functionality for a base React webpart. All React based
 * webparts are expected to inherit from this class.
 *
 * @alpha
 */
export default class ReactWebPart<TProperties> extends BaseClientSideWebPart<TProperties> {
    private _componentType;
    private _componentInstance;
    constructor(context: IWebPartContext, componentType: React.ComponentClass<IReactWebPartProps>);
    protected dispose(): void;
    render(): void;
}
